mapper.select:
SELECT s.shop_id ,s.title ,s.price ,s.created_date ,s.is_enable ,s.category_code ,s.category_etc_code ,s.point ,s.user_id ,s.size ,s.stock ,s.brand ,s.main_img ,u.user_name FROM SHOP s JOIN USER u ON s.USER_ID = u.USER_ID WHERE 1=1 AND s.IS_ENABLE = 'Y'
AND s.CATEGORY_CODE = #{category_code}
ORDER BY s.CREATED_DATE DESC LIMIT ${page_num},${page_size}

mapper.update:
    UPDATE SHOP SET
    	TITLE             = #{title}
		,CONTENTS          = #{contents}
		,PRICE             = #{price}			
		,IS_ENABLE         = #{is_enable}
		,CATEGORY_CODE	=	 #{category_code}				
		,POINT             = #{point}
		,USER_ID           = #{user_id}
		,SIZE              = #{size}
		,STOCK             = #{stock}
		,BRAND             = #{brand}
		,MAIN_IMG		   = #{main_img}
	WHERE 1=1
    	AND SHOP_ID = #{shop_id}

mapper.delete:
DELETE FROM SHOP 
WHERE 1=1
	AND SHOP_ID = #{shop_id}
